import { Component } from "react";

export class Query extends Component {
    state = {
        Field: ""
    }
    updateField = (e) => {
        const {name, value} = e.target
        this.setState({
            [name]: value
        })
        console.log("changed")
    }
    render() {
        return(
            <>
                <p>Тут можно ввести имя, чтобы сервер тебя поприветствовал</p>
                <input onChange={(e) => this.updateField(e)}></input>
                <button>Отправить</button>
            </>
        )
    }
}
