import { Component } from "react";

export class Query extends Component {
    state = {
        Field: "",
        Answer: ""
    }
    updateField = (e) => {
        const {name, value} = e.target
        this.setState({
            [name]: value
        })
        console.log("changed")
    }

    buttonClicked = () => {
        uri = `http://localhost:8083/api/user?name=${this.Field}`
        fetch(uri)
        .then(response => response.json)
        .then(date => {
            this.setState({
                Answer: date.answer
            })
        })
        .catch()
    }

    render() {
        return(
            <>
                <p>Тут можно ввести имя, чтобы сервер мог передать тебе привет</p>
                <input onChange={(e) => this.updateField(e)}></input>
                <button>Отправить</button>
            </>
        )
    }
}
