import { Component } from "react"

export class Hello extends Component {
    state = {
        text: ""
    }

    componentDidMount() {
        fetch("/api_hello/get")
        .then(reponse => reponse.json())
        .then((data) => {
            this.setState({
                text: data.result
            })
        })
        .catch(() => {
            console.log("Возникла ошибка при получении данных")
        })
    }

    render() {
        const { text } = this.state

        return(
            <>
                {text}
            </>
        )
    }
}
